#!/usr/bin/python3import simpyimport random# ******************************************************************************# Constants# ******************************************************************************CAR_ARRIVAL   = 2TRUCK_ARRIVAL = 4SIM_TIME = 100# ******************************************************************************# generator yield events to the simulator# ******************************************************************************def arrival_process(environment, vehicle, arrival_time):    while True:        # sample the time until the next event        inter_arrival = random.expovariate(lambd=1.0/arrival_time)        # yield an event to the simulator        yield environment.timeout(inter_arrival)        # the execution flow will resume here        # when the "timeout" event is executed by the "environment"        print("The event has occurred: ",              vehicle, "has arrived at", environment.now)# ******************************************************************************# create the environmentrandom.seed(42)env = simpy.Environment()# start the arrival processesenv.process(arrival_process(env, "CAR", CAR_ARRIVAL))env.process(arrival_process(env, "TRUCK", TRUCK_ARRIVAL))# simulate until SIM_TIMEenv.run(until=SIM_TIME)